import React,{useState} from "react";

export default function Form(props) {
const [text,setText] = useState("enter a text")
 
function upperCase(){
  setText(text.upperCase())
}
const changeOn=(e)=>{
setText(e.value);
}
  return (
    <div>
        <h1 className="text-center">{props.head}</h1>
      <div className="form-floating">
        <textarea
          className="form-control"
          placeholder="Leave a comment here"
          id="para" value={Text} onChange={changeOn}
          // eslint-disable-next-line react/style-prop-object
        //   style="height: 100px"
        style={{height : 230}}
        ></textarea>
      </div>
      <button className="btn btn-primary my-3" onClick={upperCase}>Change to UpperCase</button>
      {/* <button className="btn btn-primary my-3" onClick={lowerCase}>Change to </button> */}
    </div>
  );
}
